mapper.update:
UPDATE board
SET local_name = #{localName},
	board_title = #{boardTitle},
	board_content = #{boardContent}
WHERE board_no = #{boardNo}
AND member_id = #{memberId}

mapper.select:
select board_no boardNo, local_name localName, board_title boardTitle, board_content boardContent, member_id memberId, createdate, updatedate
FROM board
WHERE board_no = #{boardNo}

select board_no boardNo, local_name localName, board_title boardTitle, member_id memberId, createdate FROM board
local_name = #{localName}
ORDER BY createdate DESC LIMIT #{beginRow}, #{rowPerPage}
